// components/Home.js
import React from 'react';
import { motion } from 'framer-motion';
import profileImage from '../assets/WhatsApp Image 2025-12-23 at 9.58.59 PM.jpeg';

const Home = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <motion.div
      className="min-h-screen flex items-center justify-center px-6 md:px-12 lg:px-24 py-20 relative overflow-hidden bg-gradient-to-br from-gray-900 via-black to-gray-900"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {/* Cinematic Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Main gradient glow */}
        <motion.div
          className="absolute top-1/4 left-1/4 w-96 h-96 bg-gradient-to-r from-blue-500/20 via-purple-500/20 to-violet-500/20 rounded-full blur-3xl"
          animate={{
            scale: [1, 1.3, 1],
            opacity: [0.3, 0.6, 0.3],
            rotate: [0, 180, 360]
          }}
          transition={{ duration: 12, repeat: Infinity }}
        />

        {/* Secondary glow */}
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-gradient-to-r from-violet-500/15 to-blue-500/15 rounded-full blur-3xl"
          animate={{
            scale: [1.2, 1, 1.2],
            opacity: [0.2, 0.5, 0.2],
            rotate: [360, 180, 0]
          }}
          transition={{ duration: 15, repeat: Infinity }}
        />

        {/* Floating particles */}
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full opacity-60"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0.3, 0.8, 0.3],
              scale: [0.5, 1, 0.5]
            }}
            transition={{
              duration: 4 + Math.random() * 4,
              repeat: Infinity,
              delay: Math.random() * 2
            }}
          />
        ))}

        {/* Data points */}
        {[...Array(15)].map((_, i) => (
          <motion.div
            key={`data-${i}`}
            className="absolute text-blue-400/30 text-xs font-mono"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              opacity: [0, 1, 0],
              scale: [0.8, 1.2, 0.8]
            }}
            transition={{
              duration: 3 + Math.random() * 3,
              repeat: Infinity,
              delay: Math.random() * 5
            }}
          >
            {Math.random() > 0.5 ? '01' : '10'}
          </motion.div>
        ))}

        {/* Cinematic spotlight */}
        <motion.div
          className="absolute top-0 left-1/2 transform -translate-x-1/2 w-2 h-screen bg-gradient-to-b from-transparent via-blue-500/10 to-transparent blur-sm"
          animate={{
            opacity: [0.1, 0.3, 0.1],
            scaleX: [0.5, 1, 0.5]
          }}
          transition={{ duration: 8, repeat: Infinity }}
        />
      </div>

      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-12 items-center relative z-10">
        {/* Hero Text */}
        <motion.div className="text-center lg:text-left" variants={itemVariants}>
          <motion.div
            className="inline-flex items-center px-4 py-2 bg-primary-500/10 border border-primary-500/20 rounded-full text-primary-300 text-sm font-medium mb-6"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.5 }}
          >
            <i className="fas fa-star mr-2"></i>
            Available for opportunities
          </motion.div>

          <motion.h1
            className="text-5xl md:text-6xl lg:text-7xl font-bold mb-6"
            variants={itemVariants}
          >
            Hi, I'm{' '}
            <motion.span
              className="text-gradient relative"
              whileHover={{ scale: 1.05 }}
            >
              Anusha Kothapalli
              <motion.div
                className="absolute -bottom-2 left-0 right-0 h-1 bg-gradient-to-r from-primary-500 to-accent-500 rounded-full"
                initial={{ scaleX: 0 }}
                animate={{ scaleX: 1 }}
                transition={{ delay: 1.5, duration: 0.8 }}
              />
            </motion.span>
          </motion.h1>

          <motion.h2
            className="text-2xl md:text-3xl lg:text-4xl font-semibold text-gradient mb-8"
            variants={itemVariants}
          >
            Data Scientist | ML Engineer | Full-Stack Developer
          </motion.h2>

          <motion.div
            className="mb-8"
            variants={itemVariants}
          >
            <motion.blockquote
              className="text-2xl md:text-3xl lg:text-4xl font-light text-dark-100 italic mb-6 leading-tight"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.8, duration: 0.8 }}
            >
              "I don't just analyze data â€”<br />
              <span className="text-gradient font-medium">I make it work for people.</span>"
            </motion.blockquote>

            <motion.p
              className="text-lg md:text-xl text-dark-300 leading-relaxed max-w-2xl"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 1.0, duration: 0.8 }}
            >
              From predictive models to intelligent web systems,<br />
              I build solutions that are <span className="text-gradient font-semibold">smart, scalable, and meaningful</span>.
            </motion.p>
          </motion.div>

          <motion.div
            className="flex flex-col sm:flex-row gap-6 justify-center lg:justify-start mb-8"
            variants={itemVariants}
          >
            <motion.a
              href="#projects"
              className="btn btn-primary text-lg px-10 py-4 group"
              whileHover={{ scale: 1.05, boxShadow: "0 20px 40px rgba(100, 255, 218, 0.3)" }}
              whileTap={{ scale: 0.95 }}
            >
              <i className="fas fa-rocket mr-2 group-hover:rotate-12 transition-transform"></i>
              View My Work
            </motion.a>

            <motion.a
              href="#contact"
              className="btn btn-secondary text-lg px-10 py-4 group"
              whileHover={{ scale: 1.05, boxShadow: "0 20px 40px rgba(100, 255, 218, 0.2)" }}
              whileTap={{ scale: 0.95 }}
            >
              <i className="fas fa-envelope mr-2 group-hover:bounce"></i>
              Get In Touch
            </motion.a>
          </motion.div>

          {/* Social Links */}
          <motion.div
            className="flex justify-center lg:justify-start space-x-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.2 }}
          >
            <motion.a
              href="https://github.com/anushakprn"
              target="_blank"
              rel="noopener noreferrer"
              className="w-12 h-12 bg-dark-800 hover:bg-primary-500 rounded-full flex items-center justify-center text-dark-300 hover:text-white transition-all duration-300"
              whileHover={{ scale: 1.1, rotate: 5 }}
              whileTap={{ scale: 0.9 }}
            >
              <i className="fab fa-github text-xl"></i>
            </motion.a>
            <motion.a
              href="https://linkedin.com/in/anusha-kothapalli"
              target="_blank"
              rel="noopener noreferrer"
              className="w-12 h-12 bg-dark-800 hover:bg-primary-500 rounded-full flex items-center justify-center text-dark-300 hover:text-white transition-all duration-300"
              whileHover={{ scale: 1.1, rotate: -5 }}
              whileTap={{ scale: 0.9 }}
            >
              <i className="fab fa-linkedin text-xl"></i>
            </motion.a>
            <motion.a
              href="mailto:anusha@example.com"
              className="w-12 h-12 bg-dark-800 hover:bg-accent-500 rounded-full flex items-center justify-center text-dark-300 hover:text-white transition-all duration-300"
              whileHover={{ scale: 1.1, rotate: 5 }}
              whileTap={{ scale: 0.9 }}
            >
              <i className="fas fa-envelope text-xl"></i>
            </motion.a>
          </motion.div>
        </motion.div>

        {/* Hero Image/Visual */}
        <motion.div
          className="flex justify-center lg:justify-end"
          variants={itemVariants}
        >
          <motion.div
            className="relative"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            {/* Enhanced Animated background circles */}
            <div className="absolute inset-0 bg-gradient-to-r from-primary-500/20 to-accent-500/20 rounded-full blur-3xl animate-pulse-slow"></div>
            <motion.div
              className="absolute inset-0 bg-gradient-to-br from-accent-500/10 to-primary-500/10 rounded-full blur-2xl"
              animate={{ rotate: 360 }}
              transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
            />

            {/* Profile Image */}
            <div className="relative w-80 h-80 md:w-96 md:h-96 bg-gradient-to-br from-primary-500 to-accent-500 rounded-full flex items-center justify-center shadow-2xl overflow-hidden">
              <motion.img
                src={profileImage}
                alt="Anusha Kothapalli - Data Scientist & ML Engineer"
                className="w-full h-full object-cover rounded-full border-4 border-dark-700"
                initial={{ scale: 1.1, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ delay: 0.5, duration: 0.8 }}
                whileHover={{ scale: 1.05 }}
                onError={(e) => {
                  // Fallback to initials if image fails to load
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
              {/* Fallback initials */}
              <div className="absolute inset-0 w-full h-full bg-dark-900 rounded-full flex items-center justify-center border-4 border-dark-700" style={{display: 'none'}}>
                <span className="text-6xl md:text-7xl font-bold text-dark-50">AK</span>
              </div>

              {/* Image overlay effects */}
              <motion.div
                className="absolute inset-0 bg-gradient-to-t from-primary-500/20 via-transparent to-accent-500/20 rounded-full opacity-0"
                whileHover={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              />
            </div>

            {/* Enhanced Floating elements */}
            <motion.div
              className="absolute -top-4 -right-4 w-16 h-16 bg-primary-500 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                y: [0, -10, 0],
                rotate: [0, 5, -5, 0]
              }}
              transition={{ duration: 3, repeat: Infinity }}
              whileHover={{ scale: 1.2, rotate: 15 }}
            >
              <i className="fas fa-brain text-2xl text-white"></i>
            </motion.div>

            <motion.div
              className="absolute -bottom-4 -left-4 w-16 h-16 bg-accent-500 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                y: [0, 10, 0],
                rotate: [0, -5, 5, 0]
              }}
              transition={{ duration: 3, repeat: Infinity, delay: 1 }}
              whileHover={{ scale: 1.2, rotate: -15 }}
            >
              <i className="fas fa-code text-2xl text-white"></i>
            </motion.div>

            <motion.div
              className="absolute top-1/4 -left-8 w-12 h-12 bg-gradient-to-r from-primary-400 to-accent-400 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                x: [0, 15, 0],
                scale: [1, 1.1, 1]
              }}
              transition={{ duration: 4, repeat: Infinity, delay: 0.5 }}
              whileHover={{ scale: 1.3 }}
            >
              <i className="fas fa-robot text-lg text-white"></i>
            </motion.div>

            <motion.div
              className="absolute bottom-1/4 -right-8 w-12 h-12 bg-gradient-to-r from-accent-400 to-primary-400 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                x: [0, -15, 0],
                scale: [1, 1.1, 1]
              }}
              transition={{ duration: 4, repeat: Infinity, delay: 1.5 }}
              whileHover={{ scale: 1.3 }}
            >
              <i className="fas fa-chart-line text-lg text-white"></i>
            </motion.div>
          </motion.div>
        </motion.div>
      </div>

      {/* Scroll indicator */}
      <motion.div
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 text-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
      >
        <div className="text-dark-400 mb-2">Scroll Down</div>
        <motion.div
          className="w-6 h-10 border-2 border-dark-400 rounded-full flex justify-center"
          animate={{ y: [0, 5, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          <motion.div
            className="w-1 h-3 bg-primary-500 rounded-full mt-2"
            animate={{ y: [0, 12, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
          ></motion.div>
        </motion.div>
      </motion.div>
    </motion.div>
  );
};

export default Home;
