// components/Home.js
import React from 'react';
import { motion } from 'framer-motion';

const Home = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <motion.div
      className="min-h-screen flex items-center justify-center px-6 md:px-12 lg:px-24 py-20"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
        {/* Hero Text */}
        <motion.div className="text-center lg:text-left" variants={itemVariants}>
          <motion.h1
            className="text-5xl md:text-6xl lg:text-7xl font-bold mb-6"
            variants={itemVariants}
          >
            Hi, I'm{' '}
            <span className="text-gradient">Anusha Kothapalli</span>
          </motion.h1>

          <motion.h2
            className="text-2xl md:text-3xl lg:text-4xl font-semibold text-dark-200 mb-8"
            variants={itemVariants}
          >
            Data Scientist & Machine Learning Engineer
          </motion.h2>

          <motion.p
            className="text-lg md:text-xl text-dark-300 mb-12 leading-relaxed max-w-2xl"
            variants={itemVariants}
          >
            Passionate about leveraging AI and data science to solve complex problems.
            Experienced in machine learning, deep learning, and full-stack development with
            a proven track record of delivering impactful solutions.
          </motion.p>

          <motion.div
            className="flex flex-col sm:flex-row gap-6 justify-center lg:justify-start"
            variants={itemVariants}
          >
            <motion.a
              href="#projects"
              className="btn btn-primary text-lg px-10 py-4"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <i className="fas fa-rocket mr-2"></i>
              View My Work
            </motion.a>

            <motion.a
              href="#contact"
              className="btn btn-secondary text-lg px-10 py-4"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <i className="fas fa-envelope mr-2"></i>
              Get In Touch
            </motion.a>
          </motion.div>
        </motion.div>

        {/* Hero Image/Visual */}
        <motion.div
          className="flex justify-center lg:justify-end"
          variants={itemVariants}
        >
          <motion.div
            className="relative"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            {/* Animated background circles */}
            <div className="absolute inset-0 bg-gradient-to-r from-primary-500/20 to-accent-500/20 rounded-full blur-3xl animate-pulse-slow"></div>

            {/* Profile placeholder */}
            <div className="relative w-80 h-80 md:w-96 md:h-96 bg-gradient-to-br from-primary-500 to-accent-500 rounded-full flex items-center justify-center shadow-2xl">
              <div className="w-72 h-72 md:w-80 md:h-80 bg-dark-900 rounded-full flex items-center justify-center border-4 border-dark-700">
                <span className="text-6xl md:text-7xl font-bold text-dark-50">AK</span>
              </div>
            </div>

            {/* Floating elements */}
            <motion.div
              className="absolute -top-4 -right-4 w-16 h-16 bg-primary-500 rounded-full flex items-center justify-center shadow-lg"
              animate={{ y: [0, -10, 0] }}
              transition={{ duration: 3, repeat: Infinity }}
            >
              <i className="fas fa-brain text-2xl text-white"></i>
            </motion.div>

            <motion.div
              className="absolute -bottom-4 -left-4 w-16 h-16 bg-accent-500 rounded-full flex items-center justify-center shadow-lg"
              animate={{ y: [0, 10, 0] }}
              transition={{ duration: 3, repeat: Infinity, delay: 1 }}
            >
              <i className="fas fa-code text-2xl text-white"></i>
            </motion.div>
          </motion.div>
        </motion.div>
      </div>

      {/* Scroll indicator */}
      <motion.div
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 text-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
      >
        <div className="text-dark-400 mb-2">Scroll Down</div>
        <motion.div
          className="w-6 h-10 border-2 border-dark-400 rounded-full flex justify-center"
          animate={{ y: [0, 5, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          <motion.div
            className="w-1 h-3 bg-primary-500 rounded-full mt-2"
            animate={{ y: [0, 12, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
          ></motion.div>
        </motion.div>
      </motion.div>
    </motion.div>
  );
};

export default Home;
