// components/About.js
import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const stats = [
    { number: "5+", label: "Projects Completed", icon: "fas fa-project-diagram" },
    { number: "2+", label: "Years Experience", icon: "fas fa-calendar-alt" },
    { number: "10+", label: "Technologies", icon: "fas fa-code" },
    { number: "2", label: "Research Papers", icon: "fas fa-file-alt" }
  ];

  return (
    <motion.section
      className="section"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.3 }}
      variants={containerVariants}
    >
      <motion.div className="section-header" variants={itemVariants}>
        <h2 className="section-title">About Me</h2>
        <div className="section-divider"></div>
        <p className="section-subtitle">Get to know me better</p>
      </motion.div>

      <div className="max-w-6xl mx-auto">
        <div className="space-y-16">
          {/* Main content grid */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
          {/* About Text */}
          <motion.div variants={itemVariants}>
            <motion.p
              className="text-lg text-dark-300 mb-6 leading-relaxed"
              variants={itemVariants}
            >
              I'm a passionate <span className="text-primary-400 font-semibold">Data Scientist</span> and{' '}
              <span className="text-accent-400 font-semibold">Machine Learning Engineer</span> with expertise in
              developing AI-powered solutions. My journey in tech began with a curiosity about
              how data can drive meaningful insights and innovations.
            </motion.p>

            <motion.p
              className="text-lg text-dark-300 mb-6 leading-relaxed"
              variants={itemVariants}
            >
              Currently pursuing my Master's in Data Science, I have hands-on experience in
              building end-to-end machine learning pipelines, from data preprocessing to model
              deployment. I'm particularly interested in natural language processing, computer
              vision, and their applications in real-world scenarios.
            </motion.p>

            <motion.p
              className="text-lg text-dark-300 mb-8 leading-relaxed"
              variants={itemVariants}
            >
              When I'm not coding or analyzing data, you can find me exploring new technologies,
              contributing to open-source projects, or sharing knowledge through technical writing
              and mentoring aspiring data scientists.
            </motion.p>

            {/* Skills highlights */}
            <motion.div
              className="flex flex-wrap gap-3"
              variants={itemVariants}
            >
              {['Python', 'Machine Learning', 'Deep Learning', 'React', 'AWS'].map((skill, index) => (
                <motion.span
                  key={skill}
                  className="px-4 py-2 bg-primary-500/10 text-primary-300 rounded-full text-sm font-medium border border-primary-500/20"
                  whileHover={{ scale: 1.05, backgroundColor: 'rgba(59, 130, 246, 0.2)' }}
                  transition={{ type: "spring", stiffness: 400 }}
                >
                  {skill}
                </motion.span>
              ))}
            </motion.div>
          </motion.div>

          {/* Letter of Recommendation */}
          <motion.div
            className="lg:col-span-2 mt-16"
            variants={itemVariants}
          >
            <motion.div
              className="bg-gradient-to-br from-dark-800/50 to-dark-900/50 backdrop-blur-sm border border-dark-700/50 rounded-2xl p-8 relative overflow-hidden"
              variants={itemVariants}
            >
              {/* Background decoration */}
              <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-br from-primary-500/10 to-accent-500/10 rounded-full blur-2xl -translate-y-16 translate-x-16"></div>
              <div className="absolute bottom-0 left-0 w-24 h-24 bg-gradient-to-tr from-secondary-500/10 to-primary-500/10 rounded-full blur-xl translate-y-12 -translate-x-12"></div>

              <div className="relative z-10">
                {/* Header */}
                <motion.div
                  className="flex items-center gap-3 mb-6"
                  variants={itemVariants}
                >
                  <div className="w-12 h-12 bg-gradient-to-br from-primary-500 to-accent-500 rounded-full flex items-center justify-center">
                    <i className="fas fa-quote-left text-white text-lg"></i>
                  </div>
                  <div>
                    <h3 className="text-2xl font-bold text-dark-50">Letter of Recommendation</h3>
                    <p className="text-primary-400 text-sm font-medium">From CEO, Gen Idea Technologies</p>
                  </div>
                </motion.div>

                {/* Quote */}
                <motion.blockquote
                  className="text-lg text-dark-200 leading-relaxed mb-6 italic border-l-4 border-primary-500 pl-6"
                  variants={itemVariants}
                >
                  "The candidate demonstrated strong technical competence in machine learning concepts, along with a clear ability to apply theoretical knowledge to real-world use cases. Their commitment to continuous learning, problem-solving mindset, and analytical approach were consistently evident."
                </motion.blockquote>

                {/* Recommendation Details */}
                <motion.div
                  className="bg-dark-800/50 rounded-xl p-6 border border-dark-700/50"
                  variants={itemVariants}
                >
                  <div className="flex items-start justify-between mb-4">
                    <div>
                      <h4 className="text-lg font-semibold text-dark-50 mb-1">Internship Completion Certificate</h4>
                      <p className="text-primary-400 text-sm">Summer 2024</p>
                    </div>
                    <div className="text-right">
                      <div className="text-sm text-dark-400">Performance</div>
                      <div className="text-lg font-bold text-green-400">Good</div>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <div className="flex items-center gap-3">
                      <i className="fas fa-project-diagram text-primary-400 text-sm"></i>
                      <span className="text-dark-300">Project: <span className="text-dark-200 font-medium">"Optimizing IoT Systems: Machine Learning for Predictive Maintenance"</span></span>
                    </div>
                    <div className="flex items-center gap-3">
                      <i className="fas fa-user-tie text-accent-400 text-sm"></i>
                      <span className="text-dark-300">Supervisor: <span className="text-dark-200 font-medium">Dr. Muralidhar Kurni, IEEE Member</span></span>
                    </div>
                    <div className="flex items-center gap-3">
                      <i className="fas fa-building text-secondary-400 text-sm"></i>
                      <span className="text-dark-300">Organization: <span className="text-dark-200 font-medium">Gen Idea Technologies</span></span>
                    </div>
                  </div>
                </motion.div>

                {/* Signature */}
                <motion.div
                  className="flex justify-end mt-6"
                  variants={itemVariants}
                >
                  <div className="text-right">
                    <div className="text-dark-400 text-sm mb-1">Recommended by</div>
                    <div className="text-dark-50 font-semibold">Dr. Muralidhar Kurni</div>
                    <div className="text-primary-400 text-sm">IEEE Member</div>
                  </div>
                </motion.div>
              </div>
            </motion.div>
          </motion.div>

          {/* Stats Grid */}
          <motion.div
            className="grid grid-cols-2 gap-6"
            variants={itemVariants}
          >
            {stats.map((stat, index) => (
              <motion.div
                key={stat.label}
                className="text-center p-6 bg-gradient-to-br from-dark-800 to-dark-900 rounded-xl border border-dark-700 hover:border-primary-500/50 transition-colors duration-300"
                whileHover={{ scale: 1.05, y: -5 }}
                transition={{ type: "spring", stiffness: 300 }}
              >
                <motion.div
                  className="text-4xl md:text-5xl font-bold text-primary-400 mb-2"
                  initial={{ scale: 0 }}
                  whileInView={{ scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ delay: index * 0.1, type: "spring", stiffness: 200 }}
                >
                  {stat.number}
                </motion.div>
                <div className="text-dark-300 text-sm font-medium">{stat.label}</div>
              </motion.div>
            ))}
          </motion.div>
        </div>

        {/* Call to action */}
        <motion.div
          className="text-center mt-16"
          variants={itemVariants}
        >
          <motion.a
            href="#experience"
            className="btn btn-primary text-lg px-8 py-4"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <i className="fas fa-arrow-down mr-2"></i>
            Explore My Journey
          </motion.a>
        </motion.div>
      </div>
    </div>    </motion.section>
  );
};

export default About;
