// components/Home.js
import React from 'react';
import { motion } from 'framer-motion';
import profileImage from '../assets/WhatsApp Image 2025-12-23 at 9.58.59 PM.jpeg';

const Home = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <motion.div
      className="min-h-screen flex items-center justify-center px-6 md:px-12 lg:px-24 py-20 relative overflow-hidden bg-gradient-to-br from-gray-900 via-black to-gray-900"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {/* Cinematic Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Main gradient glow */}
        <motion.div
          className="absolute top-1/4 left-1/4 w-96 h-96 bg-gradient-to-r from-blue-500/20 via-purple-500/20 to-violet-500/20 rounded-full blur-3xl"
          animate={{
            scale: [1, 1.3, 1],
            opacity: [0.3, 0.6, 0.3],
            rotate: [0, 180, 360]
          }}
          transition={{ duration: 12, repeat: Infinity }}
        />

        {/* Secondary glow */}
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-gradient-to-r from-violet-500/15 to-blue-500/15 rounded-full blur-3xl"
          animate={{
            scale: [1.2, 1, 1.2],
            opacity: [0.2, 0.5, 0.2],
            rotate: [360, 180, 0]
          }}
          transition={{ duration: 15, repeat: Infinity }}
        />

        {/* Enhanced Floating particles */}
        {[...Array(30)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full opacity-60"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -30, 0],
              x: [0, Math.random() * 20 - 10, 0],
              opacity: [0.2, 0.9, 0.2],
              scale: [0.3, 1.2, 0.3]
            }}
            transition={{
              duration: 6 + Math.random() * 4,
              repeat: Infinity,
              delay: Math.random() * 3
            }}
          />
        ))}

        {/* Geometric shapes */}
        {[...Array(12)].map((_, i) => (
          <motion.div
            key={`shape-${i}`}
            className="absolute opacity-10"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              rotate: [0, 360],
              scale: [0.5, 1.5, 0.5],
              opacity: [0.05, 0.15, 0.05]
            }}
            transition={{
              duration: 20 + Math.random() * 10,
              repeat: Infinity,
              delay: Math.random() * 5
            }}
          >
            {i % 4 === 0 && <div className="w-8 h-8 border-2 border-blue-400 rotate-45"></div>}
            {i % 4 === 1 && <div className="w-6 h-6 bg-purple-400 rounded-full"></div>}
            {i % 4 === 2 && <div className="w-10 h-2 bg-cyan-400 rounded-full"></div>}
            {i % 4 === 3 && <div className="w-4 h-4 border border-blue-400 rotate-12"></div>}
          </motion.div>
        ))}

        {/* Enhanced Data points */}
        {[...Array(25)].map((_, i) => (
          <motion.div
            key={`data-${i}`}
            className="absolute text-blue-400/20 text-xs font-mono font-bold"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              opacity: [0, 1, 0],
              scale: [0.5, 1.5, 0.5],
              rotate: [0, 180, 360]
            }}
            transition={{
              duration: 4 + Math.random() * 4,
              repeat: Infinity,
              delay: Math.random() * 8
            }}
          >
            {['01', '10', 'AI', 'ML', 'DS', '01', '10'][Math.floor(Math.random() * 7)]}
          </motion.div>
        ))}

        {/* Circuit-like lines */}
        {[...Array(8)].map((_, i) => (
          <motion.div
            key={`circuit-${i}`}
            className="absolute h-px bg-gradient-to-r from-transparent via-blue-400/30 to-transparent"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: `${50 + Math.random() * 100}px`,
            }}
            animate={{
              opacity: [0, 0.5, 0],
              scaleX: [0, 1, 0]
            }}
            transition={{
              duration: 3 + Math.random() * 3,
              repeat: Infinity,
              delay: Math.random() * 5
            }}
          />
        ))}

        {/* Enhanced Cinematic spotlight */}
        <motion.div
          className="absolute top-0 left-1/2 transform -translate-x-1/2 w-3 h-screen bg-gradient-to-b from-transparent via-blue-500/15 to-transparent blur-sm"
          animate={{
            opacity: [0.05, 0.2, 0.05],
            scaleX: [0.3, 1.2, 0.3],
            x: [-20, 20, -20]
          }}
          transition={{ duration: 12, repeat: Infinity }}
        />

        {/* Additional ambient lights */}
        <motion.div
          className="absolute top-1/4 right-1/4 w-64 h-64 bg-gradient-to-r from-purple-500/10 to-pink-500/10 rounded-full blur-3xl"
          animate={{
            scale: [1, 1.4, 1],
            opacity: [0.1, 0.3, 0.1],
            rotate: [0, 180, 360]
          }}
          transition={{ duration: 18, repeat: Infinity }}
        />

        <motion.div
          className="absolute bottom-1/3 left-1/3 w-48 h-48 bg-gradient-to-r from-cyan-500/8 to-blue-500/8 rounded-full blur-3xl"
          animate={{
            scale: [1.2, 0.8, 1.2],
            opacity: [0.08, 0.25, 0.08],
            rotate: [360, 180, 0]
          }}
          transition={{ duration: 22, repeat: Infinity }}
        />
      </div>

      <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-16 items-center relative z-10">
        {/* Enhanced Hero Text */}
        <motion.div
          className="text-center lg:text-left space-y-8"
          variants={itemVariants}
        >
          {/* Enhanced Status Badge */}
          <motion.div
            className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-blue-500/20 via-purple-500/20 to-cyan-500/20 border border-blue-400/30 rounded-full text-blue-300 text-sm font-medium mb-8 backdrop-blur-sm"
            initial={{ opacity: 0, y: -30, scale: 0.8 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            transition={{ delay: 0.3, duration: 0.6 }}
            whileHover={{ scale: 1.05, boxShadow: "0 0 30px rgba(59, 130, 246, 0.3)" }}
          >
            <motion.div
              className="w-2 h-2 bg-green-400 rounded-full mr-3"
              animate={{ scale: [1, 1.2, 1] }}
              transition={{ duration: 2, repeat: Infinity }}
            />
            <i className="fas fa-star mr-2 text-yellow-400"></i>
            Available for opportunities
            <motion.div
              className="ml-3 w-1 h-4 bg-gradient-to-b from-blue-400 to-purple-400 rounded-full"
              animate={{ opacity: [0.5, 1, 0.5] }}
              transition={{ duration: 1.5, repeat: Infinity }}
            />
          </motion.div>

          {/* Enhanced Main Heading */}
          <motion.div
            className="space-y-4"
            variants={itemVariants}
          >
            <motion.h1
              className="text-6xl md:text-7xl lg:text-8xl font-black leading-tight"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5, duration: 0.8 }}
            >
              <span className="text-white/90">Hi, I'm</span>
              <br />
              <motion.span
                className="relative inline-block"
                whileHover={{ scale: 1.02 }}
              >
                <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
                  Anusha Kothapalli
                </span>
                {/* Enhanced underline effect */}
                <motion.div
                  className="absolute -bottom-3 left-0 right-0 h-2 bg-gradient-to-r from-blue-500 via-purple-500 to-cyan-500 rounded-full blur-sm"
                  initial={{ scaleX: 0, opacity: 0 }}
                  animate={{ scaleX: 1, opacity: 1 }}
                  transition={{ delay: 1.2, duration: 1 }}
                />
                <motion.div
                  className="absolute -bottom-3 left-0 right-0 h-1 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full"
                  initial={{ scaleX: 0 }}
                  animate={{ scaleX: 1 }}
                  transition={{ delay: 1.5, duration: 0.8 }}
                />
                {/* Glowing particles around name */}
                {[...Array(8)].map((_, i) => (
                  <motion.div
                    key={i}
                    className="absolute w-1 h-1 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full"
                    style={{
                      top: `${20 + Math.random() * 60}%`,
                      left: `${10 + Math.random() * 80}%`,
                    }}
                    animate={{
                      scale: [0, 1, 0],
                      opacity: [0, 0.8, 0],
                    }}
                    transition={{
                      duration: 2,
                      repeat: Infinity,
                      delay: Math.random() * 2,
                    }}
                  />
                ))}
              </motion.span>
            </motion.h1>
          </motion.div>

          {/* Enhanced Subtitle */}
          <motion.h2
            className="text-2xl md:text-3xl lg:text-4xl font-bold text-white/80 leading-relaxed"
            initial={{ opacity: 0, x: -30 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.8, duration: 0.8 }}
          >
            <motion.span
              className="inline-block px-4 py-2 bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-lg border border-blue-400/20 backdrop-blur-sm"
              whileHover={{ scale: 1.05 }}
            >
              Data Scientist
            </motion.span>
            <span className="mx-3 text-blue-400">|</span>
            <motion.span
              className="inline-block px-4 py-2 bg-gradient-to-r from-purple-500/10 to-cyan-500/10 rounded-lg border border-purple-400/20 backdrop-blur-sm"
              whileHover={{ scale: 1.05 }}
            >
              ML Engineer
            </motion.span>
            <span className="mx-3 text-purple-400">|</span>
            <motion.span
              className="inline-block px-4 py-2 bg-gradient-to-r from-cyan-500/10 to-blue-500/10 rounded-lg border border-cyan-400/20 backdrop-blur-sm"
              whileHover={{ scale: 1.05 }}
            >
              Full-Stack Developer
            </motion.span>
          </motion.h2>

          {/* Enhanced Quote Section */}
          <motion.div
            className="space-y-6"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.0, duration: 0.8 }}
          >
            <motion.blockquote
              className="relative text-3xl md:text-4xl lg:text-5xl font-light text-white/90 leading-tight italic"
              whileHover={{ scale: 1.01 }}
            >
              <span className="absolute -top-4 -left-4 text-6xl text-blue-400/20">"</span>
              "I don't just analyze data <br />
              <motion.span
                className="bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent font-semibold not-italic"
                animate={{ backgroundPosition: ["0% 50%", "100% 50%", "0% 50%"] }}
                transition={{ duration: 3, repeat: Infinity }}
              >
                I make it work for people.
              </motion.span>"
              <span className="absolute -bottom-8 -right-4 text-6xl text-purple-400/20">"</span>
            </motion.blockquote>

            <motion.p
              className="text-lg md:text-xl text-white/70 leading-relaxed max-w-2xl"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 1.2, duration: 0.8 }}
            >
              From predictive models to intelligent web systems,<br />
              I build solutions that are{' '}
              <motion.span
                className="bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent font-semibold"
                whileHover={{ scale: 1.05 }}
              >
                smart, scalable, and meaningful
              </motion.span>
              .
            </motion.p>
          </motion.div>

          {/* Enhanced Action Buttons */}
          <motion.div
            className="flex flex-col sm:flex-row gap-6 justify-center lg:justify-start pt-4"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.4, duration: 0.8 }}
          >
            <motion.a
              href="#projects"
              className="group relative inline-flex items-center justify-center px-12 py-5 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-bold text-lg rounded-2xl overflow-hidden shadow-2xl"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.98 }}
            >
              <motion.div
                className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-500 opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                initial={false}
              />
              <motion.div
                className="absolute inset-0 bg-gradient-to-r from-blue-400 to-purple-400 opacity-0 group-hover:opacity-20 blur-xl"
                animate={{ scale: [1, 1.2, 1] }}
                transition={{ duration: 2, repeat: Infinity }}
              />
              <i className="fas fa-rocket mr-3 group-hover:rotate-12 transition-transform duration-300"></i>
              <span className="relative z-10">View My Work</span>
              <motion.div
                className="absolute right-4 top-1/2 transform -translate-y-1/2 w-2 h-2 bg-white rounded-full opacity-0 group-hover:opacity-100"
                animate={{ x: [0, 5, 0] }}
                transition={{ duration: 1.5, repeat: Infinity }}
              />
            </motion.a>

            <motion.a
              href="#contact"
              className="group relative inline-flex items-center justify-center px-12 py-5 bg-transparent border-2 border-white/30 text-white font-bold text-lg rounded-2xl backdrop-blur-sm hover:border-white/60 transition-all duration-300"
              whileHover={{ scale: 1.05, y: -2, boxShadow: "0 0 30px rgba(255, 255, 255, 0.1)" }}
              whileTap={{ scale: 0.98 }}
            >
              <i className="fas fa-envelope mr-3 group-hover:bounce transition-transform duration-300"></i>
              <span>Get In Touch</span>
              <motion.div
                className="absolute inset-0 bg-gradient-to-r from-white/5 to-white/10 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"
              />
            </motion.a>
          </motion.div>

          {/* Enhanced Social Links */}
          <motion.div
            className="flex justify-center lg:justify-start space-x-4 pt-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.6 }}
          >
            {[
              { href: "https://github.com/anushakprn", icon: "fab fa-github", color: "hover:text-blue-400", bg: "hover:bg-blue-500/20" },
              { href: "https://linkedin.com/in/anusha-kothapalli", icon: "fab fa-linkedin", color: "hover:text-blue-600", bg: "hover:bg-blue-600/20" },
              { href: "mailto:anusha@example.com", icon: "fas fa-envelope", color: "hover:text-purple-400", bg: "hover:bg-purple-500/20" }
            ].map((social, index) => (
              <motion.a
                key={index}
                href={social.href}
                target="_blank"
                rel="noopener noreferrer"
                className={`relative w-14 h-14 bg-white/5 border border-white/10 backdrop-blur-sm rounded-2xl flex items-center justify-center text-white/70 ${social.color} ${social.bg} transition-all duration-300 overflow-hidden group`}
                whileHover={{ scale: 1.1, rotate: 5 }}
                whileTap={{ scale: 0.9 }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 1.8 + index * 0.1 }}
              >
                <motion.div
                  className="absolute inset-0 bg-gradient-to-r from-blue-500/20 to-purple-500/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                />
                <i className={`${social.icon} text-xl relative z-10`}></i>
                <motion.div
                  className="absolute inset-0 bg-white/10 rounded-2xl opacity-0 group-hover:opacity-100"
                  initial={false}
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                />
              </motion.a>
            ))}
          </motion.div>
        </motion.div>

        {/* Hero Image/Visual */}
        <motion.div
          className="flex justify-center lg:justify-end"
          variants={itemVariants}
        >
          <motion.div
            className="relative"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            {/* Enhanced Animated background circles */}
            <div className="absolute inset-0 bg-gradient-to-r from-primary-500/20 to-accent-500/20 rounded-full blur-3xl animate-pulse-slow"></div>
            <motion.div
              className="absolute inset-0 bg-gradient-to-br from-accent-500/10 to-primary-500/10 rounded-full blur-2xl"
              animate={{ rotate: 360 }}
              transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
            />

            {/* Profile Image */}
            <div className="relative w-80 h-80 md:w-96 md:h-96 bg-gradient-to-br from-primary-500 to-accent-500 rounded-full flex items-center justify-center shadow-2xl overflow-hidden">
              <motion.img
                src={profileImage}
                alt="Anusha Kothapalli - Data Scientist & ML Engineer"
                className="w-full h-full object-cover rounded-full border-4 border-dark-700"
                initial={{ scale: 1.1, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ delay: 0.5, duration: 0.8 }}
                whileHover={{ scale: 1.05 }}
                onError={(e) => {
                  // Fallback to initials if image fails to load
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
              {/* Fallback initials */}
              <div className="absolute inset-0 w-full h-full bg-dark-900 rounded-full flex items-center justify-center border-4 border-dark-700" style={{display: 'none'}}>
                <span className="text-6xl md:text-7xl font-bold text-dark-50">AK</span>
              </div>

              {/* Image overlay effects */}
              <motion.div
                className="absolute inset-0 bg-gradient-to-t from-primary-500/20 via-transparent to-accent-500/20 rounded-full opacity-0"
                whileHover={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              />
            </div>

            {/* Enhanced Floating elements */}
            <motion.div
              className="absolute -top-4 -right-4 w-16 h-16 bg-primary-500 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                y: [0, -10, 0],
                rotate: [0, 5, -5, 0]
              }}
              transition={{ duration: 3, repeat: Infinity }}
              whileHover={{ scale: 1.2, rotate: 15 }}
            >
              <i className="fas fa-brain text-2xl text-white"></i>
            </motion.div>

            <motion.div
              className="absolute -bottom-4 -left-4 w-16 h-16 bg-accent-500 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                y: [0, 10, 0],
                rotate: [0, -5, 5, 0]
              }}
              transition={{ duration: 3, repeat: Infinity, delay: 1 }}
              whileHover={{ scale: 1.2, rotate: -15 }}
            >
              <i className="fas fa-code text-2xl text-white"></i>
            </motion.div>

            <motion.div
              className="absolute top-1/4 -left-8 w-12 h-12 bg-gradient-to-r from-primary-400 to-accent-400 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                x: [0, 15, 0],
                scale: [1, 1.1, 1]
              }}
              transition={{ duration: 4, repeat: Infinity, delay: 0.5 }}
              whileHover={{ scale: 1.3 }}
            >
              <i className="fas fa-robot text-lg text-white"></i>
            </motion.div>

            <motion.div
              className="absolute bottom-1/4 -right-8 w-12 h-12 bg-gradient-to-r from-accent-400 to-primary-400 rounded-full flex items-center justify-center shadow-lg"
              animate={{
                x: [0, -15, 0],
                scale: [1, 1.1, 1]
              }}
              transition={{ duration: 4, repeat: Infinity, delay: 1.5 }}
              whileHover={{ scale: 1.3 }}
            >
              <i className="fas fa-chart-line text-lg text-white"></i>
            </motion.div>
          </motion.div>
        </motion.div>
      </div>

      {/* Scroll indicator */}
      <motion.div
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 text-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
      >
        <div className="text-dark-400 mb-2">Scroll Down</div>
        <motion.div
          className="w-6 h-10 border-2 border-dark-400 rounded-full flex justify-center"
          animate={{ y: [0, 5, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          <motion.div
            className="w-1 h-3 bg-primary-500 rounded-full mt-2"
            animate={{ y: [0, 12, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
          ></motion.div>
        </motion.div>
      </motion.div>
    </motion.div>
  );
};

export default Home;
